import React from "react";
import { useState } from 'react'
import { useAuthUser } from "react-auth-kit";
import '../styles/sections/Affiliateprofile.css'

function Affiliateprofile() {

    const auth = useAuthUser()

    const [profile, setProfile] = useState("https://scontent.fcrk3-1.fna.fbcdn.net/v/t39.30808-6/287825759_2021439201389784_2663061212977595522_n.jpg?_nc_cat=107&ccb=1-7&_nc_sid=09cbfe&_nc_eui2=AeHUOJ5Eto2mDxJzXVLYrCo8TKcasVkT5iFMpxqxWRPmIS40rxjS9EcXcWTf1Fv3BbTrENl_1B-rhwXR01nUZPPa&_nc_ohc=BZh9bqrQ96QAX_lcXHa&_nc_ht=scontent.fcrk3-1.fna&oh=00_AfAJpu-3XXM_bTOKiegLt0umNgTR1a3m8_Fs1uHlXLGrPA&oe=64A96412")
    const [firstname, setFirstname] = useState(auth().first_name);
    const [lastname, setLastname] = useState(auth().last_name);
    const [email, setEmail] = useState(auth().email);
    const [birthdate, setBirthdate] = useState(auth().birthday ? auth().birthday : "");
    const [province, setProvince] = useState(auth().province ? auth().province : "");
    const [country, setCrovince] = useState(auth().country ? auth().country : "");
    const [editview, setEditview] = useState(false)

    const updateAffiliate = (event) => {
        event.preventDefault()
        setEditview(false)

    }

    return (
        <div className="affiliateprofile-container">
            <div className="affiliateprofile-info-container">
                <div className="affiliateprofile-info-block">
                    <img alt="profile" className="affiliateprofile-info-image" src={profile}></img>
                    <div className="affiliateprofile-info-data">
                        <form onSubmit={updateAffiliate} className='affiliate-landingpage-form-container'>
                            <div className='affiliate-landingpage-two-fields'>
                                <div className='affiliate-landingpage-field'>
                                    <label htmlFor='affiliate-register-firstname'>First Name:</label>
                                    {editview ? <input type="text" placeholder={firstname}></input> : firstname}
                                </div>
                                <div className='affiliate-landingpage-field'>
                                    <label htmlFor='affiliate-register-lastname'>Last Name:</label>
                                    {editview ? <input type="text" placeholder={lastname}></input> : lastname}
                                </div>
                            </div>
                            <div className='affiliate-landingpage-two-fields'>
                                <div className='affiliate-landingpage-field'>
                                    <label htmlFor='affiliate-register-firstname'>Email:</label>
                                    {editview ? <input type="text" placeholder={email}></input> : email}
                                </div>
                                <div className='affiliate-landingpage-field'>
                                    <label htmlFor='affiliate-register-lastname'>birthday:</label>
                                    {editview ? <input type="date" placeholder={birthdate}></input> : birthdate}
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                {!editview && (<button onClick={() => { setEditview(true) }} >Edit</button>)}
                {editview && (<button onClick={() => { setEditview(false) }}>Discard</button>)}
                {editview && (<button type="submit">Submit</button>)}

            </div>
            <div className="affiliateprofile-social-container">
                Social container
            </div>
        </div>
    );
}

export default Affiliateprofile
