import React, { useCallback, useState } from "react";
import '../styles/snippets/SocialloginIG.css'
import { LoginSocialInstagram } from 'reactjs-social-login';
import { InstagramLoginButton } from 'react-social-login-buttons';
import axios from "axios";


function SocialloginIG() {

    const REDIRECT_URI = 'https://localhost:3001/dashboard/affiliate/profile'
    const [provider, setProvider] = useState();
    const [profile, setProfile] = useState();

    const onLoginStart = useCallback(() => {
        alert('login start');
    }, []);

    const onLogoutSuccess = useCallback(() => {
        setProfile(null);
        setProvider('');
        alert('logout success');
    }, []);


    return (
        <div className="socialloginig-container">
            {process.env.INSTAGRAM_CLIENT_ID}
        {process.INSTAGRAM_CLIENT_SECRET    }
            <LoginSocialInstagram
                isOnlyGetToken
                client_id={process.env.INSTAGRAM_CLIENT_ID|| ''}
                client_secret={process.env.INSTAGRAM_CLIENT_SECRET || ''}
                redirect_uri={REDIRECT_URI}
                onLoginStart={onLoginStart}
                onResolve={({ provider, data }) => {
                    setProvider(provider)
                    setProfile(data)
                    console.log(provider)
                    console.log(data)
                    axios.get(`https://graph.instagram.com/access_token?grant_type=ig_exchange_token&client_secret=${process.env.INSTAGRAM_CLIENT_SECRET}&access_token=${provider.access_token}`, {}).then((res) => {
                        console.log(res)
                    })
                }}
                onReject={(err) => {
                    console.log(err)
                }}
            >
                <InstagramLoginButton />
            </LoginSocialInstagram>
        </div>
    )
}

export default SocialloginIG
